  display: none !important;
}
.n-sei-cep{
 display: none !important;
} 
#tag-frete{
 display: none !important;
} 
.product-card {
  width: 100%;
  height: 420px; /* altura total do card */
  display: inline-block;
  font-size: 1rem !important;
  flex-direction: column;
  align-items: center;
  padding: .25em .4em;
  font-weight: 700;
  line-height: 1;
  text-align: center;
  white-space: nowrap;
  vertical-align: baseline;
  border-radius: .25rem;
}

.product-card__picture {
  width: 100%;
  height: 280px;
  overflow: hidden;
}

.product-card__picture img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.product-card__name {
  font-size: 18px;
  word-wrap: break-word;
  word-break: break-word;
  white-space: normal;
  margin-top: 1rem;
  text-align: center;
}

.product-card__prices {
  font-size: 5px;
  text-align: center;
  color: #666;
  font-weight: var(â€“font-bold);
  margin-top: 0rem
}

.product-card__buy {
  text-align: center;
}
@media (max-width: 768px) {
  .product-card {
    height: 420px;
  }
  .product-card__picture {
     height: 240px !important;
  }
  .product-card__name {
    font-size: 13px !important;
  }
  .product-card__prices {
    font-size: 12px !important;
  }
}
.button-comprar:hover {
  background-color: #000000; /* preto */
  color: #ffffff; /* branco para o texto */
}
