css:
.quantity-selector__btn-container {
    min-width: 300pxpx !important;
}
.discount-tag-price{
  display: none !important;
}
#product-actions .shipping-calculator{
  display: none !important;
}
.n-sei-cep{
 display: none !important;
} 
#tag-frete{
 display: none !important;
} 
.product-card {
  height: auto; 
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 10px;
}

.product-card__picture {
  height: 300px !important;
}

.product-card__picture img {
  width: auto;
  height: 100%;
  object-fit: cover;
}

.product-card__name{
  font-size: 14px !important;
  font-weight: 400;
  line-height: 1.2;
  margin-top: 8px;  
}
.product-card__prices{
  font-size: 13px !important;
  color: #444;
  margin-top: 4px;
}
.product-card__buy {
  text-align: center;
}
@media (max-width: 768px) {
  .product-card {
    height: 420px;
  }
  .product-card__picture {
     height: 240px !important;
  }
  .product-card__name {
    font-size: 13px !important;
  }
  .product-card__prices {
    font-size: 12px !important;
  }
}
.button-comprar:hover {
  background-color: #000000; /* preto */
  color: #ffffff; /* branco para o texto */
}
