css:
.quantity-selector__btn-container {
    min-width: 400pxpx !important;
}
.discount-tag-price{
  display: none !important;
}
#product-actions .shipping-calculator{
  display: none !important;
}
.n-sei-cep{
 display: none !important;
} 
#tag-frete{
 display: none !important;
} 
.product-card {
  height:  420px; 
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  padding: 10px;
}

.product-card__picture {
  flex: 1 1 auto;
  height: 400px; /* imagem maior */
  overflow: hidden;
}

.product-card__picture img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.product-card__name{
  font-size: 14px;
  margin-top: 8px;  
}
.product-card__prices{
  font-size: 13px !important;
  color: #444;
  margin-top: 4px;
}
.product-card__buy {
  text-align: center;
}
@media (max-width: 768px) {
  .product-card {
    height: 420px;
  }
  .product-card__picture {
     height: 240px !important;
  }
  .product-card__name {
    font-size: 13px !important;
  }
  .product-card__prices {
    font-size: 12px !important;
  }
}
.button-comprar:hover {
  background-color: #000000; /* preto */
  color: #ffffff; /* branco para o texto */
}
