css:
.quantity-selector__btn-container {
    min-width: 98.6pxpx !important;
}
.discount-tag-price{
  display: none !important;
}
#product-actions .shipping-calculator{
  display: none !important;
}
.n-sei-cep{
 display: none !important;
} 
#tag-frete{
 display: none !important;
} 
.product-card {
  height: 520px; 
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 10px;
}

.product-card__picture {
  height: 320px; 
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
}

.product-card__picture img {
  height: 100%;
  object-fit: cover;
}

.product-card__name,
.product-card__prices,
.product-card__buy {
  text-align: center;
}
@media (max-width: 768px) {
  .product-card {
    height: 420px;
  }

  .product-card__picture {
    height: 260px;
  }
}
