css:
.quantity-selector__btn-container {
    min-width: 98.8pxpx !important;
}
.discount-tag-price{
  display: none !important;
}
#product-actions .shipping-calculator{
  display: none !important;
}
.n-sei-cep{
 display: none !important;
} 
#tag-frete{
 display: none !important;
} 
.custom-product-card {
  width: 100%;
  max-width: 250px;
  margin: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 10px;
  box-sizing: border-box;
}

.custom-product-image {
  width: 100%;
  height: 280px; /* altura retangular */
  overflow: hidden;
}

.custom-product-image img {
  width: 100%;
  height: 100%;
  object-fit: cover; /* garante que a imagem preencha e mantenha proporção */
}

.custom-product-text {
  text-align: center;
  margin-top: 10px;
}

.custom-product-name {
  font-size: 14px;
  font-weight: 500;
  color: #444;
  margin: 0;
}

.custom-product-price {
  font-size: 13px;
  color: #444;
  margin-top: 4px;
}

.product-card__buy {
  text-align: center;
}
@media (max-width: 768px) {
  .product-card {
    height: 420px;
  }
  .product-card__picture {
     height: 240px !important;
  }
  .product-card__name {
    font-size: 13px !important;
  }
  .product-card__prices {
    font-size: 12px !important;
  }
}
.button-comprar:hover {
  background-color: #000000; /* preto */
  color: #ffffff; /* branco para o texto */
}
